useEffect(() => {
  connectWallet()
  LoadAlldeta();

}, [])
if(setautoref=="1"){
  console.log("hey");
}
// start
//loadweb3.js
const connectWallet = async () => {
  if(!window.ethereum)
    return;
  try {
    await window.ethereum.send('eth_requestAccounts');
    window.web3 = new Web3(window.ethereum);
  } catch (error) {
    // setInstruction("Wallet connection denied, reload the page to try again.");
    return;
  }

  const web3 = new Web3(window.ethereum);
  const accounts = await web3.eth.getAccounts();

    // setInstruction("Wallet connection denied, reload the page to try again.");

const slice = accounts[0].slice(1,5)+"..."+accounts[0].slice(-3);
setwallet(slice)
};


const LoadAlldeta = async()=>{
  if (
    typeof window.ethereum == "undefined" 
  ) {
    return;
  }

//staking abi

const web3 = new Web3(window.ethereum);
const StakingContract = new web3.eth.Contract(staking.abi,"0x6623eaAa98007EA779970A22060A7F8199C542e2");
setstakibgabi(StakingContract);
//staking abi
//trdc token abi call
const TRDcContract = new web3.eth.Contract(TRDC.abi,"0x6CD8878e6Fa3Ca6492285a189FbbE2e1eDC3B36c");
settrdcabi(TRDcContract);
//trdc token abi call
}
//this is onchnage fun
const Chnagevalue =(e)=>{
  setuserinput(e.target.value);

}
//this is for approve funtion
const approve = async (d)=>{
  d.preventDefault();
console.log("work");
const web3 = new Web3(window.ethereum);
const Amount = await web3.utils.toWei(userinput);
  try{
    const result = await trdcabi.methods.approve("0x6623eaaa98007ea779970a22060a7f8199c542e2",Amount).
  send({from:wallet.toString()}).on("transactionHash", (hash) => {
    // setapproved("Approved");
    // setstakeover(true)
  // setSuccessMessage("Hey,You are done!! ");
  window.alert("done")
})}catch{ window.alert("eee")}
  // setErrorMessage("Opps Try Again Plz!!");
  // setstakeover(false)

}
console.log(wallet);

console.log(stakingabi);
// end
return (<>
<div className="main_header">

<div className="logo">
    <img src={tokenlogo} alt="TRDC"/>
    <h3>TRDC</h3>
</div>

<div className="Blockchian_btn">
    {/* <div className="web3_btn" > */}
      <a href="#" className="web3_btn">
<svg xmlns="http://www.w3.org/2000/svg" height="24" viewBox="0 0 24 24" width="24"><path d="M0 0h24v24H0z" fill="none"/><path d="M21 18v1c0 1.1-.9 2-2 2H5c-1.11 0-2-.9-2-2V5c0-1.1.89-2 2-2h14c1.1 0 2 .9 2 2v1h-9c-1.11 0-2 .9-2 2v8c0 1.1.89 2 2 2h9zm-9-2h10V8H12v8zm4-2.5c-.83 0-1.5-.67-1.5-1.5s.67-1.5 1.5-1.5 1.5.67 1.5 1.5-.67 1.5-1.5 1.5z"/></svg>
<p onClick={connectWallet}>{wallet}</p>
</a>
{/* </div> */}
</div>






</div>

<div className="mainapp">

<div className="staking_main">
<div className="mystake">
<h2>My Staking</h2>
<h2 className="APY">100% <span className="small">APR</span></h2>
</div>

<div className="totaltoken">

<div className="totalZStaked">
