<div className="pair"><img className="token_logo" src={tokenlogo}></img>
<h3>TRDC</h3>
</div>
</div>
<div className="cliam">
<div className="Reward_token">
  <h3>CLIAMABLE REWARDS
</h3>
<p>100 TRDC</p>
</div>
<button className="Get">Claim</button>
</div>
<div className="stake" id="sa">
{/* <button>Stake</button> */}
<a href="javascript:void(0)" className="test_" onClick={approve}>
  <span>Stake</span>
